global.set("function", "login");

var loginMsg = {};

/* HTTP header */
loginMsg.headers = {
    "origin":"https://www.icloud.com"
};

/* payload */
loginMsg.payload = {
    "apple_id":flow.get("appleId"),
    "password":flow.get("password"),
