global.set("function", "create");

/* function to generate guid */
function generateGuid() {
    var guid = "";

    for (var i = 0; i < 32; i++) {
        if (i === 8 || i === 12 || i === 16 || i === 20) {
            guid += "-"
        }

        guid += Math.floor(Math.random()*0xF).toString(0xF);
    }

    return guid;
}

/* function to format a date */
function formatDate (dateString) {
    var dateArray = [
        parseInt(dateString.substring(0, 10).replace(/-/g, "")),
        parseInt(dateString.substring(0, 4)),
        parseInt(dateString.substring(5, 7)),
        parseInt(dateString.substring(8, 10)),
        parseInt(dateString.substring(11, 13)),
        parseInt(dateString.substring(14, 16)),
        0];

    return dateArray;
}

/* gets guid of list by given name */
var lists = msg.payload.Collections;
var list = {};
var k;
for (k in lists) {
    list = lists[k];

    if (list.title === flow.get("reminderList")) {
        break;
    }

    /* If the list the reminder should be put on to does not
       exist, the reminder is put on one of the existing lists. */
}

/* create URL */
var url = flow.get("remindersurl") +
        "/rd/reminders/" +
        list.guid +
        "?" +
        "usertz="+ flow.get("usertz") +
        "&lang=" + flow.get("languageCode") +
        "&dsid=" + flow.get("dsid");

/* set URL */
var reminderRequest = {"url":url};

/* HTTP header */
reminderRequest.headers = {
    "origin":"https://www.icloud.com",
    "cookie":flow.get("sessionCookie")
};

/* payload */
reminderRequest.payload = {"Reminders": {
   "guid": generateGuid(),
   "pGuid": list.guid,
   "etag": "C=42@U=" + generateGuid(),
   "createdDateExtended": 475847804,
   "completedDate": null,
   "order": 475847804,
   "title": flow.get("title"),
   "description": flow.get("description"),
   "dueDate": null,
   "dueDateIsAllDay": false,
   "startDate": null,
   "startDateIsAllDay": true,
   "startDateTz": null,
   "recurrence": null,
   "alarms": [{
      "messageType":"message",
      "onDate": null,
	  "measurement": null,
      "description": "Reminder",
      "guid": generateGuid(),
      "isLocationBased": true,
      "proximity": flow.get("proximity"),
      "structuredLocation": null
   }]
}};

/* map priority values */
if (flow.get("priority") == "1") {
    reminderRequest.payload.Reminders.priority = 1;
} else if (flow.get("priority") == "2") {
    reminderRequest.payload.Reminders.priority = 5;
} else if (flow.get("priority") == "3") {
    reminderRequest.payload.Reminders.priority = 9;
}

/* if an alarm is set */
if (undefined !== flow.get("alarm")) {
    reminderRequest.payload.Reminders.dueDate = formatDate(flow.get("alarm"));
    reminderRequest.payload.Reminders.startDate = formatDate(flow.get("alarm"));
    reminderRequest.payload.Reminders.startDateIsAllDay = false;
    reminderRequest.payload.Reminders.startDateTz = flow.get("usertz");

    reminderRequest.payload.Reminders.alarms[0].onDate = 
        formatDate(flow.get("alarm"));
}

/* map location only if latitude and longitude is given */
if (undefined !== flow.get("latitude") &&
    undefined !== flow.get("longitude")) {
    reminderRequest.payload.Reminders.alarms[0].structuredLocation = {};
    reminderRequest.payload.Reminders.alarms[0].structuredLocation.
        address = flow.get("address");
    reminderRequest.payload.Reminders.alarms[0].structuredLocation.
        latitude = flow.get("latitude");
    reminderRequest.payload.Reminders.alarms[0].structuredLocation.
        longitude = flow.get("longitude");
    reminderRequest.payload.Reminders.alarms[0].structuredLocation.
        locationName = flow.get("locationName");
}

reminderRequest.req = msg.req;
reminderRequest.res = msg.res;

return reminderRequest;

global.set("function", "logout");

var logoutRequest = {};
logoutRequest.payload = {};

/* HTTP header */
logoutRequest.headers = {
    "Origin":"https://www.icloud.com",
    "cookie":flow.get("sessionCookie")
};

logoutRequest.req = msg.req;
logoutRequest.res = msg.res;

return logoutRequest;

global.set("function", "reminder response");

var response = {};

if (undefined === flow.get("exception")) {
    response.payload = {"success":true};
} else {
    /* If the flow made it to this point,
       a reminder was created. Just create
       a warning. */
    response.payload = {
        "success":false,
        "function":flow.get("exceptionsource"),
        "message":flow.get("exception")
    };
}

response.req = msg.req;
response.res = msg.res;

return response;

global.set("function", "reminder request");

/* function to create error messages */
function createErrorMessage(message) {
    var error = {};
    error.statusCode = 400;
    error.payload = message;

    error.req = msg.req;
    error.res = msg.res;

    return error;
}

/* check input */
if (undefined === msg.payload.reminderList) {
    /* reminderList missing */
    return [null, null, createErrorMessage("The element reminderList is missing.")];
} else if (undefined === msg.payload.title) {
    /* title missing */
    return [null, null, createErrorMessage("The element title is missing.")];
} else if ((undefined === msg.payload.latitude &&
            undefined === msg.payload.longitude) &&
           undefined === msg.payload.alarm) {
    /* alarm missing - mandatory if no geoposition is given */
    return [null, null, createErrorMessage("One or more elements are missing " + 
                        "(latitude, longitude, alarm).")];
} else if ((undefined !== msg.payload.latitude &&
            undefined !== msg.payload.longitude) &&
           undefined === msg.payload.proximity) {
    /* proximity missing - mandatory if a geoposition is given */
    return [null, null, createErrorMessage("The element proximity is missing.")];
} else if (undefined === msg.payload.latitude &&
           undefined !== msg.payload.longitude) {
    /* latitude - mandatory if no alarm is given */
    return [null, null, createErrorMessage("The element latitude is missing.")];
} else if (undefined !== msg.payload.latitude &&
           undefined === msg.payload.longitude) {
    /* longitude - mandatory if no alarm is given */
    return [null, null, createErrorMessage("The element longitude is missing.")];
}

/* save input to context */
flow.set("appleId", msg.payload.appleId);
flow.set("password", msg.payload.password);
flow.set("reminderList", msg.payload.reminderList);
flow.set("priority", msg.payload.priority);
flow.set("title", msg.payload.title);
flow.set("description", msg.payload.description);
flow.set("proximity", msg.payload.proximity);
flow.set("address", msg.payload.address);
flow.set("locationName", msg.payload.locationName);
flow.set("latitude", msg.payload.latitude);
flow.set("longitude", msg.payload.longitude);
flow.set("alarm", msg.payload.alarm);

msg.req = msg.req;
msg.res = msg.res;

/* If a geoposition is given but the address is missing, call Nominatim
   to determine the address */
if (undefined !== flow.get("latitude") &&
    undefined !== flow.get("longitude") &&
    undefined === flow.get("address")) {
    global.set("function", "Nominatim request");    

    var request = {};
    request.url = 
        "http://nominatim.openstreetmap.org/reverse?format=json" +
        "&lat=" + flow.get("latitude") +
        "&lon=" + flow.get("longitude");

    request.headers = {
        "User-Agent":"node-red-icloud/1.0.0 (Node-RED)"
    }

    request.req = msg.req;
    request.res = msg.res;

    return [null, request, null];
} else {
    return [msg, null, null];
}

global.set("function", "startup");

/* get all cookies from the login response */
var cookies = msg.headers["set-cookie"];
var sessionCookie = "";
var k;

for (k in cookies) {
    sessionCookie += cookies[k].substring(0, cookies[k].indexOf(";") + 1);
}

sessionCookie = sessionCookie.substring(0, sessionCookie.lastIndexOf(";"));
/* get all cookies from login response - end */

/* store property values of the login response */
flow.set("remindersurl", msg.payload.webservices.reminders.url);
flow.set("languageCode", msg.payload.dsInfo.languageCode);
flow.set("dsid", msg.payload.dsInfo.dsid);
flow.set("sessionCookie", sessionCookie);

/* create URL */
var url = flow.get("remindersurl") +
        "/rd/startup" +
        "?" +
        "usertz=" + flow.get("usertz") +
        "&lang=" + flow.get("languageCode") +
        "&dsid=" + flow.get("dsid");

/* set URL */
var startupRequest = {"url":url};

/* HTTP header */
startupRequest.headers = {
    "origin":"https://www.icloud.com",
    "cookie":flow.get("sessionCookie")
};

startupRequest.req = msg.req;
startupRequest.res = msg.res;

return startupRequest;

var response = msg;
var addressLine = "";

if (undefined === msg.payload.error) {
    if (msg.payload.address.country_code === "ca" ||
        msg.payload.address.country_code === "gb" ||
        msg.payload.address.country_code === "us") {
        addressLine =
            (msg.payload.address.house_number || "") + " " +
            (msg.payload.address.road ||
                msg.payload.address.pedestrian ||
                "") + " " +
            (msg.payload.address.city ||
                msg.payload.address.town || 
                msg.payload.address.village ||
                msg.payload.address.hamlet ||
                "") + " " +
            (msg.payload.address.postcode || "");
    } else {
        addressLine =
            (msg.payload.address.road ||
                msg.payload.address.pedestrian ||
                "") + " " +
            (msg.payload.address.house_number || "") + " " +
            (msg.payload.address.postcode || "") + " " +
            (msg.payload.address.city ||
                msg.payload.address.town || 
                msg.payload.address.village ||
                msg.payload.address.hamlet ||
                "");
    }
}

flow.set("address", addressLine);

response.req = msg.req;
response.res = msg.res;

return response;

flow.set("exceptionsource", msg.error.source.name);
flow.set("exception", msg.error.message);

node.error(flow.get("exceptionsource"));
node.error(flow.get("exception"));
