/* check HTTP response code */
if (msg.statusCode === 200) {
    /* recreate message from input */
    var responseOk = {};
    responseOk.req = msg.req;
    responseOk.res = msg.res;
    responseOk.headers = msg.headers;
    responseOk.payload = msg.payload;
    responseOk.ldm = msg.ldm;

    /* check whether payload is filled */
    if (responseOk.payload !== '') {
        return[responseOk, null, null];
    } else {
        /* Since a JSON object cannot be created from
           an empty string, the message is passed on to
           terminal two. */
        responseOk.req = msg.req;
        responseOk.res = msg.res;
        responseOk.ldm = msg.ldm;

        return[null, responseOk, null];
    }
} else {
    /* recreate message from input */
    var responseError = {}
    responseError.req = msg.req;
    responseError.res = msg.res;
    responseError.ldm = msg.ldm;    
    responseError.statusCode = msg.statusCode;

    /* in case no HTTP response code is given */    
    if (isNaN(msg.statusCode)) {
        responseError.statusCode = 500;
    }

    responseError.payload = msg.payload;

    return[null, null, responseError];
}

var output = {};
output.req = msg.req;
output.res = msg.res;
output.payload = msg.payload;

/* function to create error messages */
function createErrorMessage(message) {
    var errors = [];
    var response = {};
    response.req = msg.req;
    response.res = msg.res;
    response.statusCode = 400;

    var error = {
        "status": 400,
        "source": { "pointer": "/reminderlists" },
        "title": 'Error while saving input to LDM',
        "detail": message
    };

    errors.push(error);

    response.payload = {
        "errors": errors
    };

    return response;
}

/* check input */
if ((undefined === msg.payload.appleId || msg.payload.appleId.length === 0) ||
    (undefined === msg.payload.password || msg.payload.password.length === 0)) {
    /* appleId or password undefined or empty */
    return [null, createErrorMessage('One or more elements are missing ' + 
                        '(appleId, password).')];
}

