/* error codes */
var errorCodes = {
    '7' : {'title': 'Invalid credentials', 'detail': 'Invalid Apple ID'},
    '1' : {'title': 'Invalid credentials',  'detail': 'Invalid password'},
    'Error: getaddrinfo EAI_AGAIN p42-remindersws.icloud.com:443': 
        {'title': 'Connection error',  'detail': 'Unable to connect to iCloud'},
    'Error: getaddrinfo EAI_AGAIN setup.icloud.com:443': 
