};

/* function to create error messages */
function createErrorMessage(status, source, title, detail) {
    var response = {};
    response.statusCode = status;
    response.req = msg.req;
    response.res = msg.res;
    response.ldm = msg.ldm;

    response.ldm.process.finished = true;

    var error = {
        "status": status,
        "source": { "pointer": source },
        "title": errorCodes[detail] ? errorCodes[detail].title : title,
        "detail": errorCodes[detail] ? errorCodes[detail].detail : detail
    };

    var errors = [];
    errors.push(error);

    response.payload = {
        "errors": errors
    };

    return response;
}

if (msg.statusCode === 200) {
    /* HTTP status code 200 OK */

    /* recreate message from input */
    var responseOk = {};
    responseOk.req = msg.req;
    responseOk.res = msg.res;
    responseOk.headers = msg.headers;
    responseOk.payload = msg.payload;
    responseOk.ldm = msg.ldm;

    /* check whether payload is filled */
    if (responseOk.payload !== '') {
        return[responseOk, null, null];
    } else {
        /* Since a JSON object cannot be created from
           an empty string, the message is passed on to
           terminal two. */
        responseOk.req = msg.req;
        responseOk.res = msg.res;
        responseOk.ldm = msg.ldm;

        return[responseOk, null, null];
    }
} else if(msg.statusCode &&
          Number.isInteger(msg.statusCode) &&
          msg.statusCode !== 200) {
    /* HTTP status codes */

    /* 
     * elements:
     *
     * msg.payload, e.g. "{"error":7}"
     * msg.statusCode, e.g. 403
     * msg.responseUrl, e.g. "https://setup.icloud.com/setup/ws/1/login"
     */
     if(msg.payload.errors) {
        /* in case an errors object exists ... */ 
        return[null, msg];
     } else {
         var errorResponse = createErrorMessage(
                                msg.statusCode,
                                msg.responseUrl,
                                msg.payload,
                                msg.payload.error);

         return[null, errorResponse, null];
     }
} else if(msg.error) {
    /* error object - mostly from catch node */

    /* 
     * elements:
     *
     * msg.error.message, e.g. "ReferenceError: a is not defined (line 4, col 4)"
     * msg.error.source.id, e.g. "60c342f9.7acd6c"
     * msg.error.source.type, e.g. "function"
     * msg.error.source.name, e.g. "set message"
     * msg.error.source.count, e.g. 1
     */
    var errorResponse = createErrorMessage(
                            500,
                            msg.error.source.type + ' ' + msg.error.source.name,
                            'Error in ' + msg.error.source.type + ' ' + msg.error.source.name,
                            msg.error.message);

    return[null, errorResponse, null];
} else if (!msg.statusCode &&
           isNaN(msg.statusCode)) {
    /* do nothing */
} else {
    /* something unexpected happened */
    var errorResponse = createErrorMessage(
                            500,
                            'Error handling',
                            'Unexpected',
                            'An error that has not been handled properly has occurred. ' +
                            'Analyse the error and provide a proper error handling for this case.');

    return[null, null, errorResponse];
}

var output = {};
output.req = msg.req;
output.res = msg.res;
output.payload = msg.payload;

/* function to create error messages */
function createErrorMessage(message) {
    var errors = [];
    var response = {};
    response.req = msg.req;
    response.res = msg.res;
    response.statusCode = 400;

    var error = {
        "status": 400,
        "source": { "pointer": "/reminderlists" },
        "title": 'Error while saving input to LDM',
        "detail": message
    };

    errors.push(error);

    response.payload = {
        "errors": errors
    };

    return response;
}

/* check input */
if ((!msg.payload.appleId || msg.payload.appleId.length === 0) ||
    (!msg.payload.password || msg.payload.password.length === 0)) {
    /* appleId or password undefined or empty */
    return [null, createErrorMessage('One or more elements are missing ' + 
                        '(appleId, password).')];
}

/* save input to LDM */
