function isJSON(jsonString) {
    try {
        JSON.parse(jsonString);
    } catch(e) {
        return false;
    }

    return true;
}

/* function to create error messages */
function createErrorMessage(status, source, detail) {
    var errors = [];
    var response = {};
    response.req = msg.req;
    response.res = msg.res;
    response.statusCode = status;

    var error = {
        "status": status,
        "source": { "pointer": source },
        "title": errorCodes[detail] ? errorCodes[detail].title : '',
        "detail": errorCodes[detail] ? errorCodes[detail].detail : detail
    };

    errors.push(error);

    response.payload = {
        "errors": errors
    };

    return response;
}

/* check HTTP response code */
if (msg.statusCode === 200) {
    /* recreate message from input */
    var responseOk = {};
    responseOk.req = msg.req;
    responseOk.res = msg.res;
    responseOk.headers = msg.headers;
    responseOk.payload = msg.payload;
    responseOk.ldm = msg.ldm;

    /* check whether payload is filled */
    if (responseOk.payload !== '') {
        return[responseOk, null, null];
    } else {
        /* Since a JSON object cannot be created from
           an empty string, the message is passed on to
           terminal two. */
        responseOk.req = msg.req;
        responseOk.res = msg.res;
        responseOk.ldm = msg.ldm;

        return[null, responseOk, null];
    }
} else {
    var errorResponse = {};
    var statusCode = msg.statusCode;
    var payload = isJSON(msg.payload) ? JSON.parse(msg.payload) : '';

    /* in case no HTTP response code is given */    
    if (isNaN(statusCode)) {
        statusCode = 500;
    }

    /* check whether there is an error object */
    if(undefined !== payload.errors) {
        /* 
         * In case there is an error already, use this error object
         * to return the root cause of the problem.
         */
        errorResponse.req = msg.req;
        errorResponse.res = msg.res;
        errorResponse.payload = payload;
        errorResponse.statusCode = statusCode;
    } else {
        errorResponse = createErrorMessage(statusCode, msg.responseUrl, msg.payload);
    }

    return[null, null, errorResponse];
}

